#!/bin/bash
echo "start" && sudo sh -c 'echo "deb http://packages.ros.org/ros/ubuntu $(lsb_release -sc) main" > /etc/apt/sources.list.d/ros-latest.list' && curl -s https://raw.githubusercontent.com/ros/rosdistro/master/ros.asc | sudo apt-key add - && sudo apt update && sudo apt -y install ros-melodic-desktop && sudo apt -y install python-rosdep python-rosinstall python-rosinstall-generator python-wstool build-essential && sudo rosdep init && rosdep update && echo "source /opt/ros/melodic/setup.bash" >> ~/.bashrc && source ~/.bashrc && echo "finished"
